mapper.update:
UPDATE top_project
SET p_name = #{p_name}, p_memberlist = #{p_memberlist}
WHERE p_num = #{p_num} AND p_m_id = #{p_m_id}

UPDATE notice
SET n_content = #{n_content}
WHERE p_num = #{p_num}

UPDATE top_project
SET p_completerate = #{rate}
WHERE p_num = #{p_num}

mapper.select:
SELECT p_num, p_m_id, p_name, p_memberlist, p_startdate, p_enddate
FROM top_project
WHERE p_num = #{p_num}

SELECT p_num, c_num, title, context, writer, TO_CHAR(indate, 'YYYY/MM/DD') indate
FROM context_project
WHERE c_num = #{c_num}

SELECT p_num, c_num, title, context, writer, TO_CHAR(indate, 'YYYY/MM/DD') indate
FROM context_project
WHERE p_num = #{p_num}

mapper.insert:
INSERT INTO notice(
	p_num, n_num, n_content
)VALUES(
	#{p_num}, n_num_seq.NEXTVAL, #{n_content}
)

INSERT INTO context_project(
	p_num, c_num, title, writer, context
)VALUES(
	#{p_num}, c_num_seq.NEXTVAL, #{title}, #{writer}, #{context}
)

mapper.update:
UPDATE notice
SET n_deldate = SYSDATE
WHERE p_num = #{p_num}
