mapper.update:
UPDATE
	TOP_USER
SET
	P_NUM_LIST = #{p_num_list}
WHERE
    ID = #{id}

  	UPDATE top_user
  	SET pw = #{pw}, email = #{email}, name = #{name}
  		 ,nickname = #{nickname} 
	  	 ,company = #{company} 
  	WHERE id = #{id}

UPDATE friend_list
SET fl = #{fl}
WHERE id = #{id}

UPDATE schedule_user
SET startdate = to_date(#{startdate}, 'yyyy-mm-dd hh24:mi'), 
	enddate = to_date(#{enddate}, 'yyyy-mm-dd hh24:mi'), 
	content = #{content}
WHERE schedule_num = #{schedule_num}

UPDATE memo
SET n_content = #{n_content}
WHERE id = #{id}

mapper.select:
SELECT id, pw, email, name, nickname, company, p_num_list
FROM top_user
WHERE id = #{id}


SELECT fl 
FROM friend_list 
WHERE id = #{id}

mapper.insert:
INSERT INTO top_user(
	id, pw, email, name, nickname
		 ,company  
)VALUES(
	#{id}, #{pw}, #{email}, #{name}
		 ,#{nickname} 
		 ,#{company} 
)

mapper.update:
UPDATE memo
SET n_deldate = SYSDATE
WHERE id = #{id}

UPDATE schedule_user
SET deldate = SYSDATE
WHERE schedule_num = #{schedule_num} 
	and id = #{id} 
	and deldate is NULL
